import { useState } from 'react';
import { useNavigate } from 'react-router-dom'

import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import SignatureCanvas from 'react-signature-canvas'


const SendOfferTab = () => {
  const navigate = useNavigate()
  const [username, setUsername] = useState("")
  const [password, setPassword] = useState("")
  const [displayAlert, setDisplayAlert] = useState(false)

  const onClick = () => {
    // Log in API

    const success = false //hard-coded in for now

    // If response is 'username already exists': 
    if (!success) {
      setDisplayAlert(true)
    }

    // Else, sign up correctly and redirect to homepage
    else {
      // Log in API request
      navigate('/')
    }

  }

  return (
    <div>
      <Form>
            <Form.Group>
              <Container>
              <Row>
                <Col  md="auto" style={{padding: "0px 10px"}}><h3>$</h3></Col>
                <Col>
              <Form.Control type="username" placeholder="Offer amount" value={username} onChange={e => setUsername(e.target.value)}/>
                </Col>
              </Row>

              </Container>


              {/* <Form.Label> <h6>Username</h6></Form.Label> */}
            </Form.Group>

            <br/>
            <Card style={{ justifyContent: "left", backgroundColor: "#f0f0f0", border: "2px solid black"}}>
              <SignatureCanvas penColor='black'
              canvasProps={{width: "700px", height: "120px", className: 'sigCanvas'}} style={{borderStyle: "solid"}}/>
            </Card>
            <i>Please sign to confirm your offer.</i>

<br/>
            <br/>

            <div class="d-flex flex-row-reverse">
            <Button variant="success" onClick={onClick}>
              Send offer
            </Button>
            </div>

      </Form>

    </div>



  )
}

export default SendOfferTab
