import { useState } from 'react';
import { useNavigate } from 'react-router-dom'

import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import SignatureCanvas from 'react-signature-canvas'


const OfferTab = () => {
  const navigate = useNavigate()
  const [username, setUsername] = useState("")
  const [password, setPassword] = useState("")
  const [displayAlert, setDisplayAlert] = useState(false)

  const onClick = () => {
    // Log in API

    const success = false //hard-coded in for now

    // If response is 'username already exists': 
    if (!success) {
      setDisplayAlert(true)
    }

    // Else, sign up correctly and redirect to homepage
    else {
      // Log in API request
      navigate('/')
    }

  }

  return (
    <div>
      <Form>
            <Form.Group>
              <Container>
              <Row>
                <Col style={{width: "10px"}}>$</Col>
                <Col>
              <Form.Control type="username" placeholder="Offer amount" value={username} onChange={e => setUsername(e.target.value)}/>
                </Col>
              </Row>

              </Container>


              {/* <Form.Label> <h6>Username</h6></Form.Label> */}
            </Form.Group>

            <br/>

            <Form.Group>
            <FloatingLabel label="Password">
              <Form.Control type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)}/>
            </FloatingLabel>
              {/* <Form.Label> <h6>Password</h6> </Form.Label> */}
            </Form.Group>

            <br/>
            <Card>
              <SignatureCanvas penColor='black'
              canvasProps={{width: "700px", height: "130px", className: 'sigCanvas'}} style={{borderStyle: "solid"}}/>
            </Card>

            <br/>

            <Button variant="primary" onClick={onClick}>
              Log in
            </Button>
      </Form>

    </div>



  )
}

export default OfferTab
